CREATE DATABASE /*!32312 IF NOT EXISTS*/`yiiweb` /*!40100 DEFAULT CHARACTER SET utf8 */;

USE `yiiweb`;

/*Table structure for table `article` */

DROP TABLE IF EXISTS `article`;

CREATE TABLE `article` (
  `id` int(11) NOT NULL auto_increment,
  `article_title` varchar(250) NOT NULL,
  `article_type` int(11) NOT NULL,
  `article_content` text NOT NULL,
  `article_author` varchar(50) NOT NULL,
  `article_click_count` int(11) NOT NULL default '0',
  `status` int(11) NOT NULL default '0',
  `attach_file` varchar(250) default NULL COMMENT '封面图片名称',
  `attach_thumb` varchar(250) default NULL COMMENT '封面图片文件',
  `image_list` text COMMENT '组图',
  `key_words` varchar(250) default NULL COMMENT '关键字',
  `fstcreate` timestamp NOT NULL default CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP,
  `lastmodify` datetime default NULL,
  PRIMARY KEY  (`id`),
  FULLTEXT KEY `fulltext_article_title` (`article_title`,`article_content`,`key_words`)
) ENGINE=MyISAM AUTO_INCREMENT=76 DEFAULT CHARSET=utf8;

/*Data for the table `article` */

insert  into `article`(`id`,`article_title`,`article_type`,`article_content`,`article_author`,`article_click_count`,`status`,`attach_file`,`attach_thumb`,`image_list`,`key_words`,`fstcreate`,`lastmodify`) values (41,'使用livequery插件对动态创建的DOM元素进行事件绑定',9,'<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	Livequery是一款基于jQuery的插件，主要作用是对动态创建的DOM元素进行事件绑定。比如我在页面中动态创建了一个新的按钮，需要通过单击新的按钮来完成某些操作，这个时候就可以用到livequery。\r\n</p>\r\n<div class=\"blog_opt\" style=\"margin:14px auto;padding:0px;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	<a href=\"http://www.helloweba.com/demo/livequery/\" class=\"btn_demo\" target=\"_blank\">查看演示DEMO</a><a href=\"http://www.helloweba.com/demo/downzip/livequery.rar\" class=\"btn_down\">下载源码</a> \r\n</div>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	先来看以个不适用livequery的例子。\r\n</p>\r\n<pre>&nbsp; <span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&lt;input</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">type</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"button\"</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">id</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"create\"</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">value</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"点击这里\"</span>&nbsp;<span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">/&gt;</span>&nbsp; <span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&lt;script</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">type</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"text/javascript\"</span><span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&gt;</span>&nbsp;\r\n$(<span class=\"js__operator\" style=\"color:#4169E1;font-weight:bold;\">function</span>()<span class=\"js__brace\" style=\"color:red;font-weight:bold;\">{</span>&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;$(<span class=\"js__string\" style=\"color:teal;\">\"#create\"</span>).click(<span class=\"js__operator\" style=\"color:#4169E1;font-weight:bold;\">function</span>()<span class=\"js__brace\" style=\"color:red;font-weight:bold;\">{</span>&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$(<span class=\"js__string\" style=\"color:teal;\">\"&lt;input&nbsp;type=\\\"button\\\"&nbsp;class=\\\"newdiv\\\"&nbsp;value=\\\"新增了一个按钮，点击看看\\\"&nbsp;/&gt;\"</span>)&nbsp;\r\n.insertAfter($(<span class=\"js__operator\" style=\"color:#4169E1;font-weight:bold;\">this</span>));&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"js__brace\" style=\"color:red;font-weight:bold;\">}</span>);&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;$(<span class=\"js__string\" style=\"color:teal;\">\".newdiv\"</span>).bind(<span class=\"js__string\" style=\"color:teal;\">\'click\'</span>,<span class=\"js__operator\" style=\"color:#4169E1;font-weight:bold;\">function</span>()<span class=\"js__brace\" style=\"color:red;font-weight:bold;\">{</span>&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(<span class=\"js__string\" style=\"color:teal;\">\'绑定了单击事件\'</span>);&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"js__brace\" style=\"color:red;font-weight:bold;\">}</span>);&nbsp; <span class=\"js__brace\" style=\"color:red;font-weight:bold;\">}</span>);&nbsp; <span class=\"html__tag_end\" style=\"color:navy;\">&lt;/script&gt;</span>&nbsp;</pre>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	通过单击页面上的按钮创建一个新的按钮，这时再去单击新按钮时却没有任何反应，但是我已经在新的按钮上通过bind方法绑定了一个click事件，原因是bind函数执行时，这些新的按钮还没创建，所以不可能绑上事件。这时需要借助livequery来完成绑定，直接将上述代码中的bind改成livequery，当然先要加载livequery插件。\r\n</p>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	其实到这里还没发现livequery有多强大，jQuery本身的live方法已经基本能实现了上述功能。\r\n</p>\r\n<pre>&nbsp;\r\n$(<span class=\"js__string\" style=\"color:teal;\">\".newdiv\"</span>).live(<span class=\"js__string\" style=\"color:teal;\">\'click\'</span>,<span class=\"js__operator\" style=\"color:#4169E1;font-weight:bold;\">function</span>()<span class=\"js__brace\" style=\"color:red;font-weight:bold;\">{</span>&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;alert(<span class=\"js__string\" style=\"color:teal;\">\'绑定了单击事件\'</span>);&nbsp; <span class=\"js__brace\" style=\"color:red;font-weight:bold;\">}</span>);&nbsp;</pre>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	关键是livequery不光只是绑定事件，它还能做很多事。\r\n</p>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	接下来看一个支持jQuery UI的draggable函数的例子：\r\n</p>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	在页面上放置一个按钮。\r\n</p>\r\n<pre>&nbsp; <span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&lt;input</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">type</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"button\"</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">id</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"drag\"</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">value</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"点击这里\"</span>&nbsp;<span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">/&gt;</span>&nbsp;</pre>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	执行操作：\r\n</p>\r\n<pre>&nbsp;\r\n$(<span class=\"js__string\" style=\"color:teal;\">\"#drag\"</span>).click(<span class=\"js__operator\" style=\"color:#4169E1;font-weight:bold;\">function</span>()<span class=\"js__brace\" style=\"color:red;font-weight:bold;\">{</span>&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;$(<span class=\"js__string\" style=\"color:teal;\">\"&lt;div&nbsp;class=\\\"dragdiv\\\"&gt;新增了一个可以拖动的DIV&lt;/div&gt;\"</span>).insertAfter($(<span class=\"js__operator\" style=\"color:#4169E1;font-weight:bold;\">this</span>));&nbsp; <span class=\"js__brace\" style=\"color:red;font-weight:bold;\">}</span>);&nbsp;\r\n$(<span class=\"js__string\" style=\"color:teal;\">\".dragdiv\"</span>).livequery(<span class=\"js__operator\" style=\"color:#4169E1;font-weight:bold;\">function</span>()<span class=\"js__brace\" style=\"color:red;font-weight:bold;\">{</span>&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;$(<span class=\"js__operator\" style=\"color:#4169E1;font-weight:bold;\">this</span>).draggable();&nbsp; <span class=\"js__brace\" style=\"color:red;font-weight:bold;\">}</span>);&nbsp;</pre>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	借助livequery和jQuery UI，可以在页面上动态创建N个可以拖动的DIV。此外完成此例必须导入jQuery UI库。\r\n</p>','我是好人',149,0,'uploads/201311/52956963c4af6.jpg','uploads/201311/thumb_52956963c4af6.jpg','a:4:{i:0;a:2:{s:6:\"fileId\";s:2:\"86\";s:4:\"file\";s:32:\"uploads/201311/52956bc22c12d.jpg\";}i:1;a:2:{s:6:\"fileId\";s:2:\"87\";s:4:\"file\";s:32:\"uploads/201311/52956bc26da39.jpg\";}i:2;a:2:{s:6:\"fileId\";s:2:\"88\";s:4:\"file\";s:32:\"uploads/201311/52956bc2968e7.jpg\";}i:3;a:2:{s:6:\"fileId\";s:2:\"94\";s:4:\"file\";s:32:\"uploads/201311/52956e3ddd7e8.jpg\";}}','Livequery,按钮,function,一个,事件,jQuery,click','2013-12-09 17:54:11','2013-12-06 16:12:25'),(42,'Fancybox丰富的弹出层效果',9,'<p style=\"font-family:Tahoma, Arial, Helvetica, sans-serif;font-size:13px;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	Fancybox是一款优秀的<a target=\"_blank\" href=\"http://jquery.com/\">jquery</a>插件，它能够展示丰富的弹出层效果。前面我们有文章介绍了facybox弹出层效果，相比facybox，fancybox显得功能更为齐全，它除了可以加载DIV，图片、图片集、Ajax数据，还能加载SWF影片，iframe页面等等。\r\n</p>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	<img alt=\"fancybox\" width=\"650\" height=\"280\" src=\"http://www.helloweba.com/attachments/fck/fancybox.jpg\" style=\"border:1px solid #D3D3D3;\" /> \r\n</p>\r\n<div class=\"blog_opt\" style=\"margin:14px auto;padding:0px;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	<a class=\"btn_demo\" target=\"_blank\" href=\"http://www.helloweba.com/demo/fancybox/\">查看演示DEMO</a><a class=\"btn_down\" href=\"http://www.helloweba.com/demo/downzip/fancybox.rar\">下载源码</a> \r\n</div>\r\n<h4 style=\"font-size:14px;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;background-color:#FDFDFD;\">\r\n	fancybox具有以下特性：\r\n</h4>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	可以加载DIV、图片、图片集、Ajax数据、SWF影片，iframe页面等。\r\n</p>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	支持键盘方向键和ESC键。\r\n</p>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	丰富的参数设置和方法调用。\r\n</p>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	可扩展性强。\r\n</p>\r\n<h4 style=\"font-size:14px;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;background-color:#FDFDFD;\">\r\n	如何使用？\r\n</h4>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	本文以DEMO中的Demo2为例，讲解fancybox的使用。\r\n</p>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	1、添加javascript引用和css文件的引用\r\n</p>\r\n<pre>&nbsp; <span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&lt;link</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">rel</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"stylesheet\"</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">type</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"text/css\"</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">href</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"fancybox.css\"</span>&nbsp;<span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">/&gt;</span>&nbsp; <span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&lt;script</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">type</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"text/javascript\"</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">src</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"js/jquery.js\"</span><span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&gt;</span><span class=\"html__tag_end\" style=\"color:navy;\">&lt;/script&gt;</span>&nbsp; <span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&lt;script</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">type</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"text/javascript\"</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">src</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"js/fancybox.js\"</span><span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&gt;</span><span class=\"html__tag_end\" style=\"color:navy;\">&lt;/script&gt;</span>&nbsp;</pre>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	2、HTML\r\n</p>\r\n<pre>&nbsp; <span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&lt;h4</span><span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&gt;</span>图片集，支持键盘方向键<span class=\"html__tag_end\" style=\"color:navy;\">&lt;/h4&gt;</span>&nbsp; <span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&lt;p</span><span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&gt;</span>&nbsp;\r\n&nbsp;&nbsp;&nbsp;<span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&lt;a</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">rel</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"group\"</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">href</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"images/b1.jpg\"</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">title</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"图片标题\"</span><span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&gt;</span><span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&lt;img</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">alt</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"\"</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">src</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"images/s1.gif\"</span>&nbsp;<span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">/&gt;</span><span class=\"html__tag_end\" style=\"color:navy;\">&lt;/a&gt;</span>&nbsp;\r\n&nbsp;&nbsp;&nbsp;<span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&lt;a</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">rel</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"group\"</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">href</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"images/b2.jpg\"</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">title</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"图片标题\"</span><span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&gt;</span><span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&lt;img</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">alt</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"\"</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">src</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"images/s2.gif\"</span>&nbsp;<span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">/&gt;</span><span class=\"html__tag_end\" style=\"color:navy;\">&lt;/a&gt;</span>&nbsp;\r\n&nbsp;&nbsp;&nbsp;<span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&lt;a</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">rel</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"group\"</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">href</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"images/b3.jpg\"</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">title</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"蓝天白云绿草\"</span><span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&gt;</span><span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&lt;img</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">alt</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"\"</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">src</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"images/s3.gif\"</span>&nbsp;<span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">/&gt;</span><span class=\"html__tag_end\" style=\"color:navy;\">&lt;/a&gt;</span>&nbsp; <span class=\"html__tag_end\" style=\"color:navy;\">&lt;/p&gt;</span>&nbsp;</pre>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	3、调用fancybox\r\n</p>\r\n<pre>&nbsp;\r\n$(<span class=\"js__operator\" style=\"color:#4169E1;font-weight:bold;\">function</span>()<span class=\"js__brace\" style=\"color:red;font-weight:bold;\">{</span>&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;$(<span class=\"js__string\" style=\"color:teal;\">\"a[rel=group]\"</span>).fancybox(<span class=\"js__brace\" style=\"color:red;font-weight:bold;\">{</span>&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"js__string\" style=\"color:teal;\">\'titlePosition\'</span>&nbsp;:&nbsp;<span class=\"js__string\" style=\"color:teal;\">\'over\'</span>,&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"js__string\" style=\"color:teal;\">\'cyclic\'</span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;:&nbsp;true,&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"js__string\" style=\"color:teal;\">\'titleFormat\'</span>&nbsp;&nbsp;&nbsp;&nbsp;:&nbsp;<span class=\"js__operator\" style=\"color:#4169E1;font-weight:bold;\">function</span>(title,&nbsp;currentArray,&nbsp;currentIndex,&nbsp;currentOpts)&nbsp;<span class=\"js__brace\" style=\"color:red;font-weight:bold;\">{</span>&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"js__statement\" style=\"color:navy;font-weight:bold;\">return</span>&nbsp;<span class=\"js__string\" style=\"color:teal;\">\'&lt;span&nbsp;id=\"fancybox-title-over\"&gt;\'</span>&nbsp;+&nbsp;(currentIndex&nbsp;+&nbsp;<span class=\"js__num\" style=\"color:red;\">1</span>)&nbsp;+&nbsp;\r\n&nbsp;<span class=\"js__string\" style=\"color:teal;\">\'&nbsp;/&nbsp;\'</span>&nbsp;+&nbsp;currentArray.length&nbsp;+&nbsp;(title.length&nbsp;?&nbsp;<span class=\"js__string\" style=\"color:teal;\">\'&nbsp;&nbsp;&nbsp;\'</span>&nbsp;+&nbsp;title&nbsp;:&nbsp;<span class=\"js__string\" style=\"color:teal;\">\'\'</span>)&nbsp;+&nbsp;<span class=\"js__string\" style=\"color:teal;\">\'&lt;/span&gt;\'</span>;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"js__brace\" style=\"color:red;font-weight:bold;\">}</span>&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"js__brace\" style=\"color:red;font-weight:bold;\">}</span>);&nbsp; <span class=\"js__brace\" style=\"color:red;font-weight:bold;\">}</span>);&nbsp;</pre>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	注意，fancybox的参数设置格式为：\'key\':\'value\'。titlePosition:设置图片标题的显示位置为图片上。cyclic：设置图片浏览方式为循环浏览。titleFormat：设置图片标题的格式，可以显示图片总数和当前图片顺序。\r\n</p>\r\n<h4 style=\"font-size:14px;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;background-color:#FDFDFD;\">\r\n	fancybox插件主要参数和方法一览表\r\n</h4>\r\n<table class=\"main_table ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\" style=\"margin:6px auto;padding:0px;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;text-align:start;background-color:#FDFDFD;\">\r\n	<tbody>\r\n		<tr class=\"table_title\">\r\n			<td width=\"25%\">\r\n				参数/方法\r\n			</td>\r\n			<td width=\"65%\">\r\n				描述\r\n			</td>\r\n			<td width=\"10%\">\r\n				默认值\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"3\" align=\"left\">\r\n				基本\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"center\">\r\n				width\r\n			</td>\r\n			<td>\r\n				设置弹出串口的宽度，当内容为swf,iframe或者单行文字时，<br />\r\n应该将\'autoDimensions\' 设置为false\r\n			</td>\r\n			<td align=\"center\">\r\n				560\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"center\">\r\n				height\r\n			</td>\r\n			<td>\r\n				设置弹出串口的高度，当内容为swf,iframe或者单行文字时，<br />\r\n应该将\'autoDimensions\' 设置为false\r\n			</td>\r\n			<td align=\"center\">\r\n				340\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"center\">\r\n				cyclic\r\n			</td>\r\n			<td>\r\n				是否循环显示，当内容为图片集时。\r\n			</td>\r\n			<td align=\"center\">\r\n				false\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"center\">\r\n				centerOnScroll\r\n			</td>\r\n			<td>\r\n				弹出窗口始终浏览器居中。\r\n			</td>\r\n			<td align=\"center\">\r\n				false\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"center\">\r\n				modal\r\n			</td>\r\n			<td>\r\n				是否使用模式窗口。当设置为true时，应该同时将：\'hideOnOverlayClick\'，\'hideOnContentClick\'，<br />\r\n\'enableEscapeButton\'， \'showCloseButton\' 设置为false，并将\'overlayShow\' 设置为true\r\n			</td>\r\n			<td align=\"center\">\r\n				false\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"center\">\r\n				titlePosition\r\n			</td>\r\n			<td>\r\n				标题的位置,可以设置为\'outside\', \'inside\' or \'over\'\r\n			</td>\r\n			<td class=\"noWrap\" align=\"center\">\r\n				\'outside\'\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"center\">\r\n				transitionIn, transitionOut\r\n			</td>\r\n			<td>\r\n				窗口显示的方式，可以设置为\'elastic\', \'fade\' or \'none\'\r\n			</td>\r\n			<td align=\"center\">\r\n				\'fade\'\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"center\">\r\n				send\r\n			</td>\r\n			<td>\r\n				方法，向服务端发送裁剪图片的数据，以便服务端接收参数进行处理，<br />\r\ne.g: cropzoom.send(\'process.php\',\'POST\',{id=1},function(r){ alert(r); });\r\n			</td>\r\n			<td align=\"center\">\r\n				&nbsp;\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"3\">\r\n				方法\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"center\">\r\n				$.fancybox.showActivity\r\n			</td>\r\n			<td>\r\n				显示加载动画\r\n			</td>\r\n			<td align=\"center\">\r\n				&nbsp;\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"center\">\r\n				$.fancybox.hideActivity\r\n			</td>\r\n			<td>\r\n				隐藏加载动画\r\n			</td>\r\n			<td align=\"center\">\r\n				&nbsp;\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"center\">\r\n				$.fancybox.close\r\n			</td>\r\n			<td>\r\n				关闭窗口\r\n			</td>\r\n			<td align=\"center\">\r\n				&nbsp;\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"center\">\r\n				$.fancybox.resize\r\n			</td>\r\n			<td>\r\n				自动调整窗口的高度使之与内容相适应\r\n			</td>\r\n			<td align=\"center\">\r\n				&nbsp;\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"center\">\r\n				Centered\r\n			</td>\r\n			<td>\r\n				是否将选区居中，即显示在容器的中心。\r\n			</td>\r\n			<td align=\"center\">\r\n				&nbsp;\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	更多参数和方法请参照<a target=\"_blank\" href=\"http://fancybox.net/api\">http://fancybox.net/api</a> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"font-family:Tahoma, Arial, Helvetica, sans-serif;font-size:13px;background-color:#FFFFFF;\">\r\n	<a href=\"http://www.elegantthemes.com/blog/freebie-of-the-week/beautiful-flat-icons-for-free#respond\"></a> \r\n</p>','我是好人',13,0,'uploads/201311/52956d8e7d95f.jpg','uploads/201311/thumb_52956d8e7d95f.jpg','a:2:{i:0;a:2:{s:6:\"fileId\";s:2:\"91\";s:4:\"file\";s:32:\"uploads/201311/52956d94e6e70.jpg\";}i:1;a:2:{s:6:\"fileId\";s:2:\"92\";s:4:\"file\";s:32:\"uploads/201311/52956d953b4ac.jpg\";}}','Fancybox,图片,参数,title,方法,images,窗口','2013-12-13 16:23:55','2013-12-06 16:12:21'),(43,'自适应的网格布局-砌墙效果',9,'<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	我们使用新浪微博的时候，在“微博精选”页面里可以看到大量的微博信息。该页面信息主要是以图片配文字说明，页面使用了自适应网格布局即砌墙效果，图片加载技术，以及滚动加载内容技术。本文先介绍砌墙效果。\r\n</p>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	其实我们做页面布局就相当于砌墙师傅在完成一堵墙的施工。我们在设计页面的时候，假如有很多相同的DIV，装载不同的内容，这些DIV都使用float：left，如果不限制这些DIV的高度，页面布局可能会出现砌墙架空的现象。<a target=\"_blank\" href=\"http://www.helloweba.com/demo/masonry/demo.html\">点击这里查看效果</a>，而经过布局优化后的页面布局效果大不一样，请看DEMO。\r\n</p>\r\n<div class=\"blog_opt\" style=\"margin:14px auto;padding:0px;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	<a href=\"http://www.helloweba.com/demo/masonry/\" class=\"btn_demo\" target=\"_blank\">查看演示DEMO</a><a href=\"http://www.helloweba.com/demo/downzip/masonry.rar\" class=\"btn_down\">下载源码</a> \r\n</div>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	本文使用David DeSandro写的一个页面布局插件<a target=\"_blank\" href=\"http://masonry.desandro.com/index.html\">Masonry</a>，该插件基于jQuery库，提供很多参数和方法，可以根据不同的需求定制不同的效果。\r\n</p>\r\n<h4 style=\"font-size:14px;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;background-color:#FDFDFD;\">\r\n	XHMTL\r\n</h4>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	首先是要引入jQuery库和Masonry插件。\r\n</p>\r\n<pre>&nbsp; <span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&lt;script</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">type</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"text/javascript\"</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">src</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"jquery.js\"</span><span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&gt;</span><span class=\"html__tag_end\" style=\"color:navy;\">&lt;/script&gt;</span>&nbsp; <span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&lt;script</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">type</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"text/javascript\"</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">src</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"jquery.masonry.min.js\"</span><span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&gt;</span><span class=\"html__tag_end\" style=\"color:navy;\">&lt;/script&gt;</span>&nbsp;</pre>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	我们仿照新浪微博构建一个html页面，DIV#container放置多个相同的DIV.photo。\r\n</p>\r\n<pre>&nbsp; <span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&lt;div</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">id</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"container\"</span><span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&gt;</span>&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&lt;div</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">class</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"photo\"</span><span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&gt;</span>&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&lt;a</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">href</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"#\"</span><span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&gt;</span><span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&lt;img</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">src</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"images/02.jpg\"</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">alt</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"\"</span>&nbsp;<span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">/&gt;</span><span class=\"html__tag_end\" style=\"color:navy;\">&lt;/a&gt;</span>&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&lt;p</span><span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&gt;</span><span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&lt;a</span>&nbsp;<span class=\"html__attr_name\" style=\"color:green;\">href</span>=<span class=\"html__attr_value\" style=\"color:maroon;\">\"#\"</span><span class=\"html__tag_start\" style=\"color:navy;font-weight:bold;\">&gt;</span>菓小菓<span class=\"html__tag_end\" style=\"color:navy;\">&lt;/a&gt;</span>：随时都要卖，还要又又美味<span class=\"html__tag_end\" style=\"color:navy;\">&lt;/p&gt;</span>&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"html__tag_end\" style=\"color:navy;\">&lt;/div&gt;</span>&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;.....N个photo...&nbsp; <span class=\"html__tag_end\" style=\"color:navy;\">&lt;/div&gt;</span>&nbsp;</pre>\r\n<h4 style=\"font-size:14px;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;background-color:#FDFDFD;\">\r\n	CSS\r\n</h4>\r\n<pre>&nbsp; <span class=\"css__id\" style=\"color:#CD5C5C;font-weight:bold;\">#container</span>{<span class=\"css__property\" style=\"color:#330066;\">width:</span><span class=\"css__number\" style=\"color:red;\">780px</span>;&nbsp;<span class=\"css__property\" style=\"color:#330066;\">margin:</span><span class=\"css__number\" style=\"color:red;\">10px</span>&nbsp;<span class=\"css__value\" style=\"color:#3366FF;\">auto</span>}&nbsp; <span class=\"css__class\" style=\"color:#CC0066;font-weight:bold;\">.photo</span>{<span class=\"css__property\" style=\"color:#330066;\">float:</span><span class=\"css__value\" style=\"color:#3366FF;\">left</span>;&nbsp;<span class=\"css__property\" style=\"color:#330066;\">width:</span><span class=\"css__number\" style=\"color:red;\">205px</span>;&nbsp;<span class=\"css__property\" style=\"color:#330066;\">margin:</span><span class=\"css__number\" style=\"color:red;\">10px</span>;&nbsp;<span class=\"css__property\" style=\"color:#330066;\">padding:</span><span class=\"css__number\" style=\"color:red;\">10px</span>;&nbsp;<span class=\"css__property\" style=\"color:#330066;\">border:</span><span class=\"css__number\" style=\"color:red;\">1px</span>&nbsp;<span class=\"css__value\" style=\"color:#3366FF;\">solid</span>&nbsp;<span class=\"css__color\" style=\"color:green;\">#d3d3d3</span>;&nbsp;\r\n&nbsp;<span class=\"css__property\" style=\"color:#330066;\">background:</span><span class=\"css__color\" style=\"color:green;\">#f7f7f7</span>;-moz-border-radius:<span class=\"css__number\" style=\"color:red;\">4px</span>;-khtml-border-radius:&nbsp;<span class=\"css__number\" style=\"color:red;\">4px</span>;-webkit-border-radius:&nbsp;<span class=\"css__number\" style=\"color:red;\">4px</span>;&nbsp;&nbsp;\r\n&nbsp;border-radius:<span class=\"css__number\" style=\"color:red;\">4px</span>;}&nbsp; <span class=\"css__class\" style=\"color:#CC0066;font-weight:bold;\">.photo</span>&nbsp;<span class=\"css__element\" style=\"color:purple;font-weight:bold;\">img</span>{<span class=\"css__property\" style=\"color:#330066;\">width:</span><span class=\"css__number\" style=\"color:red;\">205px</span>}&nbsp; <span class=\"css__class\" style=\"color:#CC0066;font-weight:bold;\">.photo</span>&nbsp;<span class=\"css__element\" style=\"color:purple;font-weight:bold;\">p</span>{<span class=\"css__property\" style=\"color:#330066;\">line-height:</span><span class=\"css__number\" style=\"color:red;\">20px</span>;&nbsp;<span class=\"css__property\" style=\"color:#330066;\">margin:</span><span class=\"css__number\" style=\"color:red;\">4px</span>&nbsp;<span class=\"css__value\" style=\"color:#3366FF;\">auto</span>}&nbsp;</pre>\r\n<h4 style=\"font-size:14px;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;background-color:#FDFDFD;\">\r\n	jQuery\r\n</h4>\r\n<pre>&nbsp;\r\n$(<span class=\"js__operator\" style=\"color:#4169E1;font-weight:bold;\">function</span>()<span class=\"js__brace\" style=\"color:red;font-weight:bold;\">{</span>&nbsp;\r\n&nbsp;&nbsp;$(<span class=\"js__string\" style=\"color:teal;\">\'#container\'</span>).masonry(<span class=\"js__brace\" style=\"color:red;font-weight:bold;\">{</span>&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;itemSelector&nbsp;:&nbsp;<span class=\"js__string\" style=\"color:teal;\">\'.photo\'</span>,&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;columnWidth&nbsp;:&nbsp;<span class=\"js__num\" style=\"color:red;\">247</span>&nbsp;\r\n&nbsp;&nbsp;<span class=\"js__brace\" style=\"color:red;font-weight:bold;\">}</span>);&nbsp; <span class=\"js__brace\" style=\"color:red;font-weight:bold;\">}</span>);&nbsp;</pre>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	调用masonry插件，配置参数itemSelector对应的是class为photo的div，columnWidth为247，columnWidth的值是由.photo的宽度+padding*2+margin*2+border*2得到的。更多参数配置请访问masonry插件网站：<a target=\"_blank\" href=\"http://masonry.desandro.com/docs/options.html\">http://masonry.desandro.com/docs/options.html</a> \r\n</p>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	本文只介绍砌墙布局，接下来我会抽时间结合本文把图片加载技术和滚屏加载技术相关文章发布在helloweb.com博客里，敬请关注。\r\n</p>','我是好人',9,0,'uploads/201311/52957152aaeb0.jpg','uploads/201311/thumb_52957152aaeb0.jpg','a:1:{i:0;a:2:{s:6:\"fileId\";s:3:\"119\";s:4:\"file\";s:32:\"uploads/201311/5295713f07309.jpg\";}}','页面,布局,Masonry,photo,border,插件,效果','2013-12-12 12:12:25','2013-12-06 16:13:56'),(44,'多级下拉菜单：superfish的应用',9,'<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	superfish是一款可以制作多级下拉菜单的jquery插件，支持水平和垂直方向的菜单，下拉的菜单不会被标签select遮挡，支持动态下拉效果，如垂直向下伸展，支持阴影效果（IE6除外），用户可定制样式。\r\n</p>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	<img alt=\"\" width=\"650\" height=\"240\" src=\"http://www.helloweba.com/attachments/fck/superfish.gif\" style=\"border:1px solid #D3D3D3;\" /> \r\n</p>\r\n<div class=\"blog_opt\" style=\"margin:14px auto;padding:0px;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FDFDFD;\">\r\n	<a href=\"http://www.helloweba.com/demo/superfish/\" class=\"btn_demo\" target=\"_blank\">查看演示DEMO</a><a href=\"http://www.helloweba.com/demo/downzip/superfish.rar\" class=\"btn_down\">下载源码</a> \r\n</div>\r\n<h4 style=\"font-size:14px;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;background-color:#FDFDFD;\">\r\n	主要特性：\r\n</h4>\r\n<h4 style=\"font-size:14px;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;background-color:#FDFDFD;\">\r\n	如何使用？\r\n</h4>\r\n<p style=\"text-indent:2em;color:#424242;font-family:\'Microsoft YaHei\', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;bac