insert  into `site`(`id`,`site_title`,`site_content`,`fstcreate`,`lastmodyfy`,`status`) values (1,'联系我们','<h1>\r\n	联系我\r\n</h1>\r\n<p>\r\n	1、新浪微博&nbsp;<a href=\"http://weibo.com/xinwenzhigege\" target=\"_blank\">@新闻志哥哥</a> \r\n</p>\r\n<p>\r\n	2、腾讯微博&nbsp;<a href=\"http://t.qq.com/z205817y\" target=\"_blank\">@新闻志哥哥</a> \r\n</p>\r\n<p>\r\n	3、E-mail：513245459@qq.com（请将#改成@）\r\n</p>\r\n<p>\r\n	4、QQ：513245459\r\n</p>\r\n<p>\r\n	5、个人作品：米库&nbsp;<a href=\"http://miucool.com/\" target=\"_blank\">MiuCool.com</a> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>','2013-12-10 15:15:59','0000-00-00 00:00:00',1),(2,'关于我们','<h1>\r\n	关于MiuCool\r\n</h1>\r\n<p>\r\n	MiuCool关注WEB开发领域知识和技术，致力于WEB技术的推广和应用，分享WEB开发中常见的WEB应用及WEB开发技术知识相关文章，现已发展成为一个独立的WEB技术博客。\r\n</p>\r\n<p>\r\n	长期以来，MiuCool本着“分享 • 务实”的精神，为广大WEB技术爱好者提供了许多当今实用性强、高质量的博文。\r\n</p>\r\n<p>\r\n	MiuCool的文章内容来源大多是站长根据多年工作经验收集整理的，注重实用性、综合性和原创性，文章内容难易层次偏向中高级，适合WEB开发人员技能的提升，因此MiuCool的读者应该具备一定的WEB开发技术基础，以及了解后台语言如PHP。MiuCool文章内容不会讲解WEB基础知识，如果读者还是WEB初学者，建议先通过其他方式打好基础然后再来阅读MiuCool，相信会对读者有很大的提升。\r\n</p>\r\n<p>\r\n	MiuCool将一如既往的坚持发表WEB技术高品质文章，让我们一起努力，提升自己，创造未来。\r\n</p>\r\n<p>\r\n	大多数文章需要读者具备以下技能：\r\n</p>\r\n<p>\r\n	1、熟悉HTML，CSS知识，懂得写简单网页。\r\n</p>\r\n<p>\r\n	2、熟悉Javascript基础，熟悉jQuery框架的基本应用。\r\n</p>\r\n<p>\r\n	3、了解PHP，至少应具备熟悉PHP常用函数以及MySQL数据库基本操作技能。\r\n</p>\r\n<h4>\r\n	关于版权\r\n</h4>\r\n<p>\r\n	MiuCool文章大部分由新闻志哥哥亲自整理撰写的，是辛勤劳动无私奉献的成果，如非特别说明均系本站原创文章。MiuCool允许网络转载所有文章，所有源码允许复制修改，但不得用于商业用途，转载请注明出处转自MiuCool，否则视为侵权。\r\n</p>\r\n<h4>\r\n</h4>\r\n<h1>\r\n	联系我\r\n</h1>\r\n<p>\r\n	1、新浪微博&nbsp;<a href=\"http://weibo.com/xinwenzhigege\" target=\"_blank\">@新闻志哥哥</a> \r\n</p>\r\n<p>\r\n	2、腾讯微博&nbsp;<a href=\"http://t.qq.com/z205817y\" target=\"_blank\">@新闻志哥哥</a> \r\n</p>\r\n<p>\r\n	3、E-mail：513245459@qq.com（请将#改成@）\r\n</p>\r\n<p>\r\n	4、QQ：513245459\r\n</p>\r\n<p>\r\n	5、个人捣鼓：米库&nbsp;<a href=\"http://miucool.com/\" target=\"_blank\">MiuCool.com</a> \r\n</p>\r\n<div>\r\n	<br />\r\n</div>\r\n<br />\r\n<p>\r\n	<a href=\"http://miucool.com\" target=\"_blank\"></a> \r\n</p>','2013-12-10 15:16:02','0000-00-00 00:00:00',0);

/*Table structure for table `upload` */

DROP TABLE IF EXISTS `upload`;

CREATE TABLE `upload` (
  `id` int(10) unsigned NOT NULL auto_increment,
  `user_id` int(10) unsigned NOT NULL default '0' COMMENT '用户名',
  `scope` enum('content','image') NOT NULL default 'content' COMMENT '范围',
  `folder` smallint(5) unsigned NOT NULL default '0' COMMENT '文件夹',
  `real_name` varchar(255) NOT NULL default '' COMMENT '原始文件名称',
  `file_name` char(100) NOT NULL default '' COMMENT '带路径文件名',
  `thumb_name` varbinary(255) NOT NULL default '' COMMENT '缩略图',
  `save_path` varchar(100) NOT NULL default '' COMMENT '保存路径',
  `save_name` varchar(100) NOT NULL default '' COMMENT '保存文件名不带路径',
  `hash` char(32) NOT NULL default '' COMMENT 'hash',
  `file_ext` char(5) NOT NULL default 'jpg' COMMENT '扩展名称',
  `file_mime` varchar(50) NOT NULL default '' COMMENT '文件头信息',
  `file_size` int(10) unsigned NOT NULL default '0' COMMENT '文件大小',
  `down_count` int(10) unsigned NOT NULL default '0' COMMENT '下载次数',
  `access` varchar(255) NOT NULL default '' COMMENT '权限控制',
  `create_time` int(10) unsigned NOT NULL default '0' COMMENT '上传时间',
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=155 DEFAULT CHARSET=utf8 COMMENT='附件';

